DROP TABLE IF EXISTS `mm_c_skills`;DROP TABLE IF EXISTS `mm_c_senses`;DROP TABLE IF EXISTS `mm_c_environ`;DROP TABLE IF EXISTS `mm_skills`;DROP TABLE IF EXISTS `mm_c_attr`;DROP TABLE IF EXISTS `mm_attributes`;DROP TABLE IF EXISTS `mm_senses`;DROP TABLE IF EXISTS `mm_environments`;DROP TABLE IF EXISTS `mm_minions`;DROP TABLE IF EXISTS `mm_creatures`;DROP TABLE IF EXISTS `mm_types`;CREATE TABLE `mm_types` (	`id` int(11) NOT NULL AUTO_INCREMENT,	`category` varchar(255) NOT NULL,	PRIMARY KEY (`id`),	UNIQUE (`category`)) ENGINE=InnoDB; CREATE TABLE `mm_creatures` (	`id` int(11) NOT NULL AUTO_INCREMENT,	`name` varchar(255) NOT NULL,	`hp` smallint unsigned NOT NULL,	`ac` smallint unsigned NOT NULL,	`cr`  float(5) unsigned NOT NULL,	`type_id`int(11) NOT NULL, 	PRIMARY KEY (`id`),	UNIQUE (`name`),	FOREIGN KEY (`type_id`) 		REFERENCES `mm_types` (`id`)		ON DELETE CASCADE) ENGINE=InnoDB;CREATE TABLE `mm_attributes` (	`id` int(11) NOT NULL AUTO_INCREMENT,	`name` varchar(255) NOT NULL,	PRIMARY KEY (`id`),	UNIQUE (`name`)) ENGINE=InnoDB;CREATE TABLE `mm_c_attr` (	`creature_id` int(11) NOT NULL,	`attribute` int(11) NOT NULL,	`stat` smallint unsigned,	PRIMARY KEY (`creature_id`, `attribute`),	FOREIGN KEY (`creature_id`)		REFERENCES `mm_creatures` (`id`)		ON DELETE CASCADE,	FOREIGN KEY (`attribute`)		REFERENCES `mm_attributes` (`id`)		ON DELETE CASCADE) ENGINE=InnoDB;CREATE TABLE `mm_skills` (	`id` int(11) NOT NULL AUTO_INCREMENT,	`name` varchar(255) NOT NULL,	`attribute` int(11) NOT NULL,	PRIMARY KEY (`id`),	UNIQUE (`name`),	FOREIGN KEY (`attribute`)		REFERENCES `mm_attributes` (`id`)		ON DELETE CASCADE) ENGINE=InnoDB;CREATE TABLE `mm_c_skills` (	`skill_id` int(11) NOT NULL,	`creature_id` int(11) NOT NULL,	`bonus` smallint NOT NULL,	PRIMARY KEY (`skill_id`,`creature_id`),	FOREIGN KEY (`skill_id`)		REFERENCES `mm_skills` (`id`)		ON DELETE CASCADE,	FOREIGN KEY (`creature_id`)		REFERENCES `mm_creatures` (`id`)		ON DELETE CASCADE) ENGINE=InnoDB;CREATE TABLE `mm_senses` (	`id` int(11) NOT NULL AUTO_INCREMENT,	`name` varchar(255) NOT NULL,	PRIMARY KEY (`id`),	UNIQUE (`name`)) ENGINE=InnoDB;CREATE TABLE `mm_c_senses` (	`creature_id` int(11) NOT NULL,	`sense_id` int(11) NOT NULL,	`distance` smallint unsigned,	PRIMARY KEY (`creature_id`,`sense_id`),	FOREIGN KEY (`creature_id`)		REFERENCES `mm_creatures` (`id`)		ON DELETE CASCADE,	FOREIGN KEY (`sense_id`)		REFERENCES `mm_senses` (`id`)		ON DELETE CASCADE) ENGINE=InnoDB;CREATE TABLE `mm_environments` (	`id` int(11) NOT NULL AUTO_INCREMENT,	`name` varchar(255) NOT NULL,	PRIMARY KEY (`id`),	UNIQUE (`name`)) ENGINE=InnoDB;CREATE TABLE `mm_c_environ` (	`creature_id` int(11) NOT NULL,	`envir_id` int(11) NOT NULL,	PRIMARY KEY (`creature_id`,`envir_id`),	FOREIGN KEY (`creature_id`)		REFERENCES `mm_creatures` (`id`)		ON DELETE CASCADE,	FOREIGN KEY (`envir_id`)		REFERENCES `mm_environments` (`id`)		ON DELETE CASCADE)ENGINE=InnoDB;CREATE TABLE `mm_minions` (	`boss_id` int(11) NOT NULL,	`minion_id` int(11) NOT NULL,	PRIMARY KEY (`boss_id`,`minion_id`),	FOREIGN KEY (`boss_id`)		REFERENCES `mm_creatures` (`id`)		ON DELETE CASCADE,	FOREIGN KEY (`minion_id`)		REFERENCES `mm_creatures` (`id`)		ON DELETE CASCADE)ENGINE=InnoDB;